                                                <i class="fa-solid fa-book"></i>
                                                glibc {{ flavor.glibc }}
                                            </span>
                                        {% endif %}
                                    </div>
                                    <span class="flavor-badge flavor-size-badge">
                                        <i class="fa-solid fa-file-zipper"></i>
                                        {{ flavor.package_size }}
                                    </span>
                                    {% if perms.pack.delete_packageentry %}
